  { name: "Fitbit", url: "https://www.fitbit.com/", icon_url: "https://www.google.com/s2/favicons?sz=256&domain=fitbit.com", category: "health" }
]

apps.each do |a|
  Webapp.find_or_create_by!(name: a[:name]) do |w|
    w.url = a[:url]
    w.icon_url = a[:icon_url]
    w.category = a[:category]
  end
end

puts "Seeded #{Webapp.count} webapps"